// This function will recursively search any object or array for "quizData"
function findQuizData(obj) {
  let results = [];

  if (Array.isArray(obj)) {
    for (const item of obj) {
      results = results.concat(findQuizData(item));
    }
  } else if (obj && typeof obj === 'object') {
    for (const [key, value] of Object.entries(obj)) {
      if (key === 'quizData' ) {
        results.push(value);
      } else {
        results = results.concat(findQuizData(value));
      }
    }
  }

  return results;
}

